import React from "react";
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import { mapapikey } from "../../config";
import { useState } from "react";
import CurrencyInput from 'react-currency-input-field';
const Adform = ({ action, type }) => {
    const [ad, setAd] = useState({
        photos: [],
        uploading: false,
        price: "",
        address: "",
        bedrooms: "",
        bathrooms: "",
        bedrooms: "",
        carpark: "",
        landsize: "",
        type: "",
        title: "",
        description: "",
        loading: false
    })
    return (
        <>
            <div className="container">


                <div className="mb-3 form-control ">
                    <GooglePlacesAutocomplete selectProps={{
                        defaultInputValue: ad?.address,
                        placeholder: 'Enter any location',
                        onChange: ({ value }) => {
                            setAd({ ...ad, address: value.description })
                        }
                    }} apiOptions='in' apiKey={mapapikey} />
                </div>
                <div> {JSON.stringify(ad, null, 4)}</div>
                <div className="mb-3 form-control">
                    <CurrencyInput className='mt-5 form-control' placeholder="Please enter a number" defaultValue={ad.price} onValueChange={(value) => setAd({ ...ad, price: value })} />
                        <input className="form-control mb-3 " placeholder="Enter How Many Bedrooms" min='0' value={ad.bedrooms} onChange={e=>setAd({...ad,bedrooms:e.target.value})} required/>
                </div>



            </div>
        </>
    )
}

export default Adform;
