import React from "react";
import SideBar from ".././Navigation/Sidebar";
import { useState } from "react";
const CreateAd = () => {
  const [sell, setSell] = useState(false);
  const [rent, setRent] = useState(false);
  const handleSell = ()=>{
    setSell(true);
    setRent(false);
  }
  const handleRent = ()=>{
    setRent(true);
    setSell(false);

  }
  return (
    <>
      <h1 className="display-p1 text-light bg-primary">CreateAd</h1>
      <SideBar />
      <div className="d-flex justify-content-center align-items-center vh-100" style={{marginTop:'-14%'}}>
        <div className="col-lg-6"> <button className="btn btn -primary " onClick={handleSell}>Sell</button>
        {sell && 'Show Sell options'}
        </div>
        <div className="col-lg-6"> <button className="btn btn -primary " onClick={handleRent}>Rent</button>
        {rent && 'Show rent options'}
        </div>


      </div>
    </>
  );
};

export default CreateAd;
